<script>
export default {
  props: {
    id: String 
  },
  data: function() {
    const fromStore = this.$store.state.spotify.songs.filter((e) => {
      return e.id === this.$props.id
    })[0]
    const temperature = (fromStore.energy>0.5) ? 'warm' : 'cold'
    let pointyness = ''
    if (fromStore.valence < 0.3) {
      pointyness = 'pointy'
    } else if (fromStore.valence>0.3 && fromStore.valence<0.7) {
      pointyness = 'pointy-smooth'
    } else {
      pointyness = 'smooth'
    }
    const sourceFile = pointyness+' '+temperature
    return {
      name: fromStore.name,
      valence: fromStore.valence,
      energy: fromStore.energy,
      sourceFile
    }
  }
}
</script>

